  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.scss']
})
export class HomePageComponent implements OnInit, OnDestroy {



constructor(private animeService: AnimesService) {}
animes: any;
ngOnInit(){
